import React, { useState } from 'react'
import './App.css'

const App =()=>{

  const [oldvalue, newvalue] = useState("")

  const [sub,newsub] = useState("")



  const submit=(e)=>{
    newsub(oldvalue)

  }


  const fs = require('fs');

  let student = { 
      name: 'Mike',
      age: 23, 
      gender: 'Male',
      department: 'English',
      car: 'Honda' 
  };

  let data = JSON.stringify(student, null, 2);
  const write =()=>{
    fs.writeFile('student-3.json', data, (err) => {
      if (err) throw err;
      console.log('Data written to file');
  });

  console.log('This is after the write call');

  }


  return(
    <>
    <h1>Paste the json </h1>
    <textarea type="textarea" placeholder="paste the json" value={oldvalue} onChange={(e)=>newvalue(e.target.value)} className="area" ></textarea>

    <button onClick={submit}>Submit</button>

    <div className="rightside">{sub}</div>
    </>
  )
}

export default App
