import React, { useState } from 'react'
import './App.css'

const App =()=>{

  const [oldvalue, newvalue] = useState("")

  const [sub,newsub] = useState("")

  const submit=(e)=>{
    newsub(oldvalue)
  }

  const updateInpuArray =(index, newvalue)=>{
    const updateArray =[...oldvalue]
    useState({
      sub:updateArray,
    })

  }


  return(
    <>
    <h1>Paste the json </h1>
    <textarea type="textarea" placeholder="paste the json" value={oldvalue} onChange={(e)=>newvalue(e.target.value)} className="area" ></textarea>

    <button onClick={submit}>Submit</button>

    <div className="rightside">{sub}</div>
    </>
  )
}

export default App
