  const [oldvalue, newvalue] = useState("")

  const [sub,newsub] = useState("")

  const fs = require('fs');

  let student = { 
    name: 'Mike',
    age: 23, 
    gender: 'Male',
    department: 'English',
    car: 'Honda' 
};


  const submit=(e)=>{
    newsub(oldvalue)
    // write(fs)
    localStorage.setItem('data',JSON.stringify(oldvalue))
    fs.writeFile = ('student.json', oldvalue, (err) => {
      if (err) throw err;
      console.log('Data written to file');
  });


  }



  const write =(fs)=>{

   let data = JSON.stringify(student, 2);
      fs.writeFile = ('student.json', data, (err) => {
        if (err) throw err;
        console.log('Data written to file');
    });

    console.log('This is after the write call');

  }


  return(
    <>
    <h1>Paste the json </h1>
    <textarea type="textarea" placeholder="paste the json" value={oldvalue} onChange={(e)=>newvalue(e.target.value)} className="area" ></textarea>

    <button onClick={submit}>Submit</button>

    <div className="rightside">{sub}</div>
    </>
  )
}

export default App
