import './App.css'


const App =()=>{


  const [oldvalue, newvalue] = useState("")

  const [sub,newsub] = useState("")

  const SAMPLE_JSON = { 
    "one": 1, 
    "two": true, 
    "3": "three" 
  } 
  const LOCALSTORAGE_KEY = 'someJson' 

  const submit=(e)=>{
    newsub(oldvalue)
    // localStorage.setItem("Data",oldvalue)
    const validJson = this.validateJson(this.state.json) 
    if (!validJson) return; 
    window.localStorage.setItem( 
      LOCALSTORAGE_KEY, 
      validJson 
    ) 
  }
  // const loadJson = () => { 
