import React,{useEffect, useRef} from 'react'
import './Colum.css'
import * as d3 from 'd3'



// cbxnbxnbxzcbcbcxnmbnmxbmnbxcnmxbzxczehsisgbsfdnbbsbm x nbbsbciwdkkboodnc mvxbddvhbvbaegnc  hhdh



const Colum =(data)=>{
    let xdim=750
    let ydim=500
    let margin={top:80 ,bottom:80, left:120, right:120}

 
  
    // console.log(data.data.month)
    let xdata=data

    // let month=["jan","feb","wed","thursday","march","april","may","june","july","august"]
   
    // let xdata=month
    // const vals=[23,55,22,12,23,8,45,74,88,31,90]
   
    let ydata=data
    const canvas = useRef(null)
    let SAMPLE_JSON = []

    // let rendering=[oldvalue]
    // const val= [oldvalue.split(",")]

//   let mon =[]


    useEffect(()=>{ 
        // newm(data);

        // console.log(data.map)
       
        // data.data.map((val,index)=>{
        //     month.push(val.month)
        // })
        // xdata= mon

        const svg = d3.select(canvas.current) 
        addAxes(svg);
        addBars(svg);
        addText(svg);

        

    },[xdim,ydim,margin,xdata,ydata])


    const newm =(data)=>{
      
        // let xdata=month
        // console.log(data.month)
        // SAMPLE_JSON = data
        // data.data.map((val,index)=>{
        //     mon.push(val.month)
        //     // [val].map((ind,ite)=>{
        //     //     console.log(ind.month)
        //     // })
        // })
        // console.log(mon)
        // mon.push([SAMPLE_JSON].map(item=>(
        //   item.month
        // )))
        // console.log(mon[0])
        // console.log(SAMPLE_JSON)
        // mon.push([SAMPLE_JSON].map(item=>(
        //   console.log(item.month)
        // )))
        // console.log(mon[0])
    }

    const addAxes=(svg)=>{
      
        const xAxis= d3.axisBottom(xscale);

        svg.append("g")
            .call(xAxis)
            .attr("transform",`translate(0, ${ydim})`)
            .selectAll("text")
            .attr("text-anchor","start")
            .attr("transform","rotate(45)")

     

        const yAxis = d3.axisLeft(yscale)

        svg.append("g")
            .call(yAxis)
            .attr("transform",`translate(${margin.left},0)`)

    }

    const addBars =(svg)=>{
        const linearScale = d3.scaleLinear()
        .domain([0,d3.max(ydata.data.map(d=>(d.sales)))])
        .range([0,ydim])

        const scaleYData = ydata.data.map(vals=>{
            return linearScale(vals.sales)
        })
        // console.log(scaleYData)
        svg.selectAll("rect")
        .data(scaleYData)
        .enter()
        .append("rect")
        .attr("width",xscale.bandwidth())
        .attr("height",(d)=>{
            return d 
        })
        .attr("x",(d,i)=>{
            return xscale(xdata.data.map(d=>(d.month))[i])
        })
        .attr("y",(d)=>{
            return ydim -d
        })
        .attr("fill","dodgerblue")
        .attr("stroke","black")
    }

    const addText=(svg)=>{
        svg.append("text")
        .text("Scales")
        .attr("x",(margin.left+ margin.right+xdim)/2)
        .attr("y",(margin.top -100)/2)


        svg.append("text")
        .text("weeek data") 
        .attr("text-anchor","middle")
        .attr("x",-(margin.top+margin.bottom+ydim)/3)
        .attr("y",margin.left/2)
        .attr("transform",`rotate(-90,${margin.left/2}, ${margin.top/2})`)
    }

    const xscale = d3.scaleBand()
    .domain(xdata.data.map(d=>(d.month)))
    .range([margin.left,xdim +margin.left])
    .padding(0.2)

    const yscale= d3.scaleLinear()
    .domain([0,d3.max(ydata.data.map(d=>(d.sales)))])
    .range([ydim,0])
    
 
    return(
        <>
            <div className="canvas">
            <svg viewBox={`0 0 ${xdim +margin.left+ margin.right} ${ydim+ 
            margin.top + margin.bottom}`} preserveAspectRatio="xMinYmin" width="100%" height="100%" ref={canvas}>

            </svg>

            </div>
        </>
    )
    

}
export default Colum




