import './App.css'


const App =()=>{

  const [oldvalue, newvalue] = useState("")

  const [sub,newsub] = useState("")

  const submit=(e)=>{
    newsub(oldvalue)
    localStorage.setItem("Data",oldvalue)
    let SAMPLE_JSON = {
     oldvalue
    }
  }

  const LOCALSTORAGE_KEY = 'someJson'
  let json ={}

  const validateJson= (json)=> { 
    let validJson 

    try{ 
      validJson = JSON.stringify(JSON.parse(json), null, 2) 
    } catch(e) { 
      throw e 
    } 

    return validJson 
  } 


  const loadJson = () => { 
    const json = window.localStorage.getItem(LOCALSTORAGE_KEY) || JSON.stringify(SAMPLE_JSON, null, 2) 
    this.setState({ json }) 
  } 
  const saveJson = () => { 
    const validJson = this.validateJson(json) 

    if (!validJson) return; 

    window.localStorage.setItem( 
      LOCALSTORAGE_KEY, 
      validJson 
    ) 
  } 




  return(
    <>
    <h1>Paste the json </h1>
    <textarea type="textarea" placeholder="paste the json" value={oldvalue} onChange={(e)=>newvalue(e.target.value)} className="area" ></textarea>

    <button onClick={submit}>Submit</button>

    <div className="rightside">{sub}</div>
    </>
  )
}

export default App
