import React, { Component, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { scaleBand, select } from 'd3';





const Columnchart =(data)=>{
    const svgRef=useRef();
    const wrapperRef= useRef();

    const dimes= useResizeObserver(wrapperRef);

    useEffect(()=>{
        const svg= select(svgRef.current)
        const {width,height} =dimes|| wrapperRef.current.getBoundingClientRect();

        const xScale = scaleBand().domain(data.map(d=>d.month))
        .range([0,width]);
    },data)

    // console.log(data)
    return(
        <>  
        <div ref={wrapperRef} style={{marginBottom:"2rem"}}>
        <svg ref={svgRef}> 
            <g className="x-axis"/>
            <g className="y-axis"/>
        </svg>

        </div>


        </>
    )

}

export default Columnchart
